jQuery(document).ready(function($){var file_frame;$(".gh-image-picker").on("click",function(event){var picker=$(this);event.preventDefault();if(file_frame){file_frame.open();return}file_frame=wp.media.frames.file_frame=wp.media({title:"Select a image to upload",button:{text:"Use this image"},multiple:false});file_frame.on("select",function(){var attachment=file_frame.state().get("selection").first().toJSON();var content=jQuery("#email-body");$("#"+picker.prop("id")+"-src").val(attachment.url);content.find(".active").find("img").attr("src",attachment.url);$("#"+picker.prop("id")+"-alt").val(attachment.alt);content.find(".active").find("img").attr("alt",attachment.alt);$("#"+picker.prop("id")+"-title").val(attachment.title);content.find(".active").find("img").attr("title",attachment.title)});file_frame.open()})});