(function(){const isSameOrigin=destination=>{const origin=new URL(window.location);destination=new URL(destination);return origin.protocol===destination.protocol&&origin.host===destination.host};function initAllFrames(){document.querySelectorAll("iframe:not(.gh-from-iframe)").forEach((frame,i)=>{let src=frame.src||frame.dataset.src;if(src&&src.match(/\/gh\//)&&isSameOrigin(src)){frame.id=`gh-frame-${i}`;frame.classList.add("gh");frame.style.height=frame.contentWindow.document.body.offsetHeight+"px";frame.style.height=frame.contentWindow.document.body.offsetHeight+"px"}})}function addEvent(event,callback){if(!window.addEventListener){window.attachEvent("on"+event,callback)}else{window.addEventListener(event,callback,false)}}function resizeAllFrames(){document.querySelectorAll("iframe.gh").forEach((frame,i)=>{frame.contentWindow.postMessage({action:"getFrameSize",id:frame.id},"*")})}function receiveMessage(event){resizeFrame(event.data)}function resizeFrame(data){if(data.height){let frame=document.getElementById(data.id);if(frame){frame.style.height=data.height+"px";frame.style.width=data.width+"px"}}}addEvent("load",initAllFrames);addEvent("message",receiveMessage);addEvent("resize",resizeAllFrames);window.fullFrame=initAllFrames})();